var domReady=function(callback){document.readyState==="interactive"||document.readyState==="complete"?callback():document.addEventListener("DOMContentLoaded",callback);};function getRandomInt(min,max){return Math.floor(Math.random()*(max-min))+min;}function checkVisible(elm){var rect=elm.getBoundingClientRect();var viewHeight=Math.max(document.documentElement.clientHeight,window.innerHeight);return!(rect.bottom<0||rect.top-viewHeight>=-200);}domReady(function(){function randomizeData(){var graphItems=document.getElementsByClassName("graph-item");var changeInterval=getRandomInt(10,60);console.log("data changed.");for(var i=0;i<graphItems.length;++i){var currentItem=graphItems[i];var height=parseInt(currentItem.style.height,10);if(height>300&&getRandomInt(1,100)>50){currentItem.style.height=(height-changeInterval).toString()+"px";}else if(height<70&&getRandomInt(1,100)>50){currentItem.style.height=(height+changeInterval).toString()+"px";}else if(height>=70||height<=300){currentItem.style.height=getRandomInt(height-changeInterval,height+changeInterval).toString()+"px";}}}var graphs=document.getElementsByClassName("graphs")[0];if(window.getComputedStyle(graphs).getPropertyValue("display")!=="none"){window.setInterval(randomizeData,3000);}var descriptions=document.getElementsByClassName("description");window.onscroll=function(){for(var i=0;i<descriptions.length;i++){if(checkVisible(descriptions[i])){if(descriptions[i].className!=="description fadeInRight"){descriptions[i].className="description fadeInRight";}}}}});